body.dark .download-video-panel {
  box-shadow: 0 4px 12px 0 rgba(0, 0, 0, 0.2);
}
body.dark .download-video-panel {
  background-color: #282828;
  color: #eee;
}
.download-video-panel.open {
  transform: translateX(-50%);
}
.download-video-panel .be-textbox,
.download-video-panel .be-textarea {
  flex-grow: 1;
}
.download-video-panel-header {
  display: flex;
  align-items: center;
  border-bottom: 1px solid #8882;
  padding: 6px 0;
  margin: 0 6px;
}
.download-video-panel-header .title {
  font-size: 16px;
  font-weight: 600;
  flex-grow: 1;
  margin: 0 8px;
}
.download-video-panel-header .be-button {
  padding: 4px;
}
.download-video-panel-content {
  overflow: auto;
  scrollbar-width: none !important;
  overscroll-behavior: contain;
  display: flex;
  align-items: stretch;
  flex-direction: column;
  flex: 1 0 0;
  padding: 12px 6px;
  align-items: flex-start;
}
.download-video-panel-content::-webkit-scrollbar {
  height: 0 !important;
  width: 0 !important;
}
.download-video-panel-content > :not(:first-child) {
  margin-top: 12px;
}
.download-video-panel .download-video-config-item {
  display: flex;
  align-items: center;
}
.download-video-panel .download-video-config-item .download-video-config-title {
  margin-right: 8px;
}
.download-video-panel .download-video-config-item.error {
  color: #e57373;
}
.download-video-panel .download-video-config-section {
  align-self: stretch;
}
.download-video-panel .download-video-config-description {
  color: #888d;
  margin-top: 4px;
}
.download-video-panel .download-video-config-description a {
  color: var(--theme-color-70);
}
.download-video-panel-footer {
  display: flex;
  align-items: center;
  border-top: 1px solid #8882;
  padding: 6px 0;
  margin: 0 6px;
  justify-content: center;
}
.download-video-panel .run-download {
  font-size: 13px;
  padding: 6px 12px;
